mapper.select:
select id, username, usertype, windowsaccount,
displayname , nickname , namezhspell, namezhshortspell, givenname , middlename , familyname ,
gender,
authntype, mobile, email, workemail, workphonenumber,
employeenumber, division, costcenter, organization, departmentid, department, jobtitle, joblevel,
createdby, createddate, modifiedby, modifieddate,
status from mxk_userinfo where (1=1)
and username = #{username}
and employeenumber = #{employeeNumber}
and usertype = #{userType}
and displayname like '%${displayName}%'
and departmentid = #{departmentId}
order by sortorder

mapper.update:
update mxk_userinfo set
islocked = #{isLocked},
modifieddate = current_timestamp where id = #{id}

update mxk_userinfo set
islocked = #{isLocked}, badpwdcount = 0,
unlockdate = current_timestamp, modifieddate = current_timestamp where id = #{id}

update mxk_userinfo set
password = #{password}, decipherable = #{decipherable},
passwordsettype = 0, passwordlastsettime = current_timestamp where id = #{id}

update mxk_userinfo set
sharedsecret = #{sharedSecret}, sharedcounter = #{sharedCounter},
modifieddate = current_timestamp where id = #{id}

update mxk_userinfo set
apploginpassword = #{appLoginPassword},
modifieddate = current_timestamp where id = #{id}

update mxk_userinfo set
protectedapps = #{protectedApps},
modifieddate = current_timestamp where id = #{id}

update mxk_userinfo set
passwordquestion = #{passwordQuestion}, passwordanswer = #{passwordAnswer},
modifieddate = current_timestamp where id = #{id}

update mxk_userinfo set
authntype = #{authnType},
modifieddate = current_timestamp where id = #{id}
