mapper.update:
    	update mxk_groups set
    		status	=	'2'  
    	where	instid   =   #{instId} 

			and	id	=	#{id}


			and	groupname	=	#{groupName}

    	update mxk_groups
    	set status='2' 
    	where instid   =   #{instId} 
    	   and id in

				#{item}

mapper.select:
select  distinct
    g.*
from
    mxk_userinfo u,
    mxk_groups   g,
    mxk_group_member gm
where   u.instid    =   #{instId} 
    and g.instid    =   #{instId} 
    and gm.instid   =   #{instId} 
    and gm.memberid = #{value}
    and u.id        = #{value}
    and gm.type     in( 'USER','USER-DYNAMIC')
    and gm.groupid  =   g.id    
    and gm.memberid =   u.id
